export const trash = (req, res, next) => {
    NoteService.sendToTrash(req.params._id, req.body.userId)
      .then(data => {
        res.status(HttpStatus.ACCEPTED).json({
          code: HttpStatus.ACCEPTED,
          data: data,
          message: 'OK'
        });
      })
      .catch(error => {
        next(error);
      });
  };
  

// export const recovertrash = async (req, res, next) => {
