export const recovertrash = (req, res, next) => {
    NoteService.recoverFromTrash(req.params._id, req.body.userId)
    .then(data => {
    res.status(HttpStatus.ACCEPTED).json({
    code: HttpStatus.ACCEPTED,
    data: data,
    message: 'OK'
    });
    })
    .catch(error => {
    next(error);
    });
    };

