  return new Promise((resolve, reject) => {
    Note.findByIdAndUpdate({ _id, userId: userId }, { trash: true }, { new: true })
      .then(data => {
        resolve(data);
      })
      .catch(err => {
        reject(new Error(err));
      });
  });
};




