import {Schema , model} from 'mongoose';

const noteSchema = new Schema(
    {
        titel: {
            type: String
        },
        description: {
            type: String
        },
        color: {
            type: String,
        },
        archive: {
            type: Boolean, 
           default : false
        },
        trash: {
            type: Boolean,
            default : false
        },
        userId: {
            type: String
        }

    },
    {
        timestamps: true
    }
);

export default model('Notes', noteSchema);

